mapper.select:
select distinct sch_day
from joa_schedule
where sch_mov_title = #{sch_mov_title}
and sch_branch = #{sch_branch}
order by sch_day asc

select sch_theater,sch_start_hour,sch_start_min,sch_end_hour,sch_end_min
from joa_schedule
where sch_mov_title = #{sch_mov_title}
and sch_branch = #{sch_branch}
and sch_day = #{sch_day}
order by sch_theater asc
